mapper.select:
SELECT COUNT(commu_idx)
  FROM community
 WHERE 1=1



			AND commu_subject LIKE #{search_keyword}




			AND commu_content LIKE #{search_keyword}

SELECT c.commu_idx, m.member_name, c.commu_subject, COUNT(cl.commu_like_idx) AS commu_like, c.commu_date FROM community c LEFT OUTER JOIN commu_like cl ON c.commu_idx = cl.commu_idx JOIN member m ON m.member_idx = c.member_idx WHERE 1=1
AND commu_subject LIKE #{search_keyword}
AND commu_content LIKE #{search_keyword}
GROUP BY commu_idx
ORDER BY commu_date DESC
ORDER BY commu_readcount DESC
ORDER BY commu_like DESC
LIMIT #{pageInfo.startRow}, #{pageInfo.listLimit}
