mapper.insert:
INSERT INTO member
VALUES (
		(SELECT IFNULL(MAX(CAST(m.member_idx as unsigned)), 0) + 1 
		   FROM member m)
		,#{member_id}
		,#{member_pass}
		,#{member_name}
		,#{member_gender}
		,#{member_birth}
		,#{member_email}
		,#{member_space}
		,'Y'
		,DATE_FORMAT(now(), "%Y%m%d%H%i%s")
		,''
		,'N'
		,'N'
	   )

mapper.select:
SELECT *
  FROM member
 WHERE member_id = #{member_id}
   AND member_pass = #{member_pass}
