mapper.insert:
INSERT INTO study
VALUES (
		(SELECT IFNULL(MAX(CAST(s.study_idx as unsigned)), 0) + 1 
		   FROM study s)
		,(SELECT member_idx
		    FROM member
		   WHERE member_id = #{member_id})
		,#{study.study_subject}
		,#{study.study_content}
		,#{study.study_type}
		,#{study.study_time}
		,#{study.study_num_of_people}
		,#{study.study_contact_type}
		,#{study.study_space}
		,DATE_FORMAT(now(), "%Y%m%d%H%i%s")
		,'0'
		,'Y'
	   )

mapper.select:
SELECT COUNT(study_idx)
  FROM study
 WHERE 1=1

  	 AND study_type = #{study.study_type}


  	 AND study_time = #{study.study_time}


     AND study_num_of_people = #{study.study_num_of_people}


 	 AND study_contact_type = #{study.study_contact_type}


	 AND study_space = #{study.study_space}

SELECT study_idx,
	   study_subject,
	   study_type,
	   study_time,
	   study_num_of_people,
	   study_contact_type,
	   study_space,
	   study_date,
	   study_readcount,
	   study_status
  FROM study
 WHERE 1=1

  	 AND study_type = #{study.study_type}


  	 AND study_time = #{study.study_time}


     AND study_num_of_people = #{study.study_num_of_people}


 	 AND study_contact_type = #{study.study_contact_type}


	 AND study_space = #{study.study_space}

  LIMIT #{pageInfo.startRow}, #{pageInfo.listLimit}

SELECT COUNT(study_idx)
  FROM study
 WHERE 1=1

  	 AND study_type = #{study.study_type}


  	 AND study_time = #{study.study_time}


     AND study_num_of_people = #{study.study_num_of_people}


 	 AND study_contact_type = #{study.study_contact_type}


	 AND study_space = #{study.study_space}



	     AND study_status = 'Y'


	     AND study_status = 'N'





				AND study_subject LIKE #{search_keyword}




				AND study_content LIKE #{search_keyword}

SELECT study_idx,
	   study_subject,
	   study_type,
	   study_time,
	   study_num_of_people,
	   study_contact_type,
	   study_space,
	   study_date,
